import React from "react";
import '../cssFiles/products.css'
import DArrow from '../../img/dArrowIcon.png'

const Cat = ({category,currentCat}) => {
    log
  return (
    <div
      className="catButton"
    >
      <a href={`/products?cat=${category.categoryId}`}>
        <article
          style={{
            cursor: "pointer",
            color: category.categoryId === currentCat?.cat ? "crimson" : "black",
            textDecoration:
              category.categoryId === currentCat?.cat ? "underline" : "none",
          }}
        >
          {category.categoryName}
          {/* arrow */}
          <img src={DArrow} />
        </article>
      </a>
    </div>
  );
};

export default Cat;
